// ExpenseTable.js
import React from 'react';

const ExpenseTable = ({ expenses, deleteExpens }) => {
    return (
        <div className="transactions-list">
            {expenses.length === 0 ? (
                <p className="empty-state">No transactions yet</p>
            ) : (
                expenses.map((expense, index) => (
                    <div key={index} className={`transaction-item ${expense.amount > 0 ? 'income' : 'expense'}`}>
                        <div className="transaction-content">
                            <div className="transaction-description">{expense.text}</div>
                            <div className="transaction-amount">
                                {expense.amount > 0 ? '+' : ''}â‚¹{Math.abs(expense.amount)}
                            </div>
                        </div>
                        <button 
                            className="delete-button" 
                            onClick={() => deleteExpens(expense._id)}
                            aria-label="Delete transaction"
                        >
                            &times;
                        </button>
                    </div>
                ))
            )}
        </div>
    );
};

export default ExpenseTable;
