import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProgressTracker = () => {
  const [expenses, setExpenses] = useState(0);
  const [limit, setLimit] = useState(1000); // Example limit

  useEffect(() => {
    axios.get('/api/expenses/total').then((res) => {
      setExpenses(res.data.total);
    });
  }, []);

  const percentage = ((expenses / limit) * 100).toFixed(1);

  return (
    <div className="mt-6">
      <h2 className="text-xl font-bold mb-2">Budget Progress</h2>
      <div className="w-full bg-gray-200 rounded-full h-4">
        <div
          className={`h-4 rounded-full ${percentage > 100 ? 'bg-red-500' : 'bg-green-500'}`}
          style={{ width: `${Math.min(percentage, 100)}%` }}
        ></div>
      </div>
      <p className="mt-1 text-sm">
        {expenses} spent of {limit} ({percentage}%)
        {percentage > 100 && <span className="text-red-600 ml-2">Over budget!</span>}
      </p>
    </div>
  );
};

export default ProgressTracker;
